function PigEdge(properties) {
    var self = this;
    self.properties = properties;    
    self.hasSampleData = function() {
        if (self.properties.sampleOutput) {
            return true;
        } 
        return false;
    };

    self.hasSchema = function() {
        if (self.properties.schema) {
            return true;
        }
        return false;
    };

    self.sampleOutput = function() {
        var sol = self.properties.sampleOutput;
        var result = sol.map(function(so, i) {
            return {
                data: so.split("\n").map(function(record, j) {
                    return {fields: record.split("\u0001")};
                })
            };
        });
        return result;
    };

    self.schema = properties.schema;
}

template:
{{#hasSampleData}}
<h4>Sample Data: </h4>
{{#sampleOutput}}
<table class="table table-condensed table-bordered table-hover">
  {{#hasSchema}}
  <thead>
    <tr style="background-color: #fcf8e3;">
      {{#schema}}
      <td><strong>{{alias}}</strong></td>
      {{/schema}}
    </tr>
  </thead>
  {{/hasSchema}}
  {{^hasSchema}}
  <thead><tr style="background-color: #fcf8e3;"><td colspan="200">&nbsp;</td></tr></thead>
  {{/hasSchema}}
  <tbody>
    {{#data}}
    <tr>
    {{#fields}}
      <td>{{.}}</td>
    {{/fields}}
    </tr>
    {{/data}}
  </tbody>
</table>
{{/sampleOutput}}
{{/hasSampleData}}
{{^hasSampleData}}
    <table class="table table-condensed table-bordered table-hover"><tr><td>No sample output available at this time.</td></tr></table>
{{/hasSampleData}}
